import React from "react";
import DefCard from './DefCard';
import Card from './Card';

const Board2 = (props) => {
  const {definitions} = props;
  console.log(props)
  const dropOff = (e) => {
    e.preventDefault();
    const card_id = e.dataTransfer.getData('card_id');
    const card = document.getElementById(card_id);
    card.style.display = "block";
    e.target.appendChild(card);
    e.target.style.height="auto";
  }
  const findArrInArr = (val,arr) => {
    let hash = {};
    for(let i = 0 ; i < arr.length; i += 1) {
        hash[arr[i]] = i;
    }
    if(hash.hasOwnProperty(val)) {
     return hash[val];
    }
  }
  let defs_shuffled = []
  for (let i of Object.keys(definitions)){
    defs_shuffled.push(definitions[`${i}`])
  }
  console.log(defs_shuffled)
  let defs = []
  for (let i of [" 0"," 1"," 2"," 3"," 4"," 5"]){
    defs.push(definitions[i])
  }
  console.log(defs)
  const dragOver = (e) => {
    e.preventDefault();
  }

  return(
    <div
      id="board_2"
      onDrop={dropOff}
      onDragOver={dragOver}
      className="board"
    >
      {
        defs_shuffled.length === 6 ? defs_shuffled.map((def,i) => {return(<DefCard id={`def${defs.indexOf(def)}`} className="card" draggable="true" text={def}/>)}) : <Card id="7" className="card" draggable="false" text="Loading..."/>
      }
    </div>
  )
}
export default Board2;
