import React, { Component } from "react";
import "./App.css";
import Register from "./Components/Register/Register";
import Signin from "./Components/Signin/Signin";
import Navigation from "./Components/Navigation/Navigation";
import Board from './Components/Board';
import Board2 from './Components/Board2';
import Card from './Components/Card';



class App extends Component {
  _isMounted = false;
  constructor() {
    super();

    this.state = {


      //To Route the between signin forms and dashboard
      route: 'signin',
      isSignedIn: false,

      user: {

        id: '',
        name: '',
        email: '',
        entries: 0,
        joined: '',
        words:[],
        definitions:[],
        defs_shuffled:[]
      }

    }
  }
  componentDidMount(){
  this._isMounted = true;
  setInterval(() => {
           this.setState(() => {
               return { unseen: "does not display" }
           });
       }, 1000);
  }
  //Function to load the user when register form is inputed(pass function to Register Component)
  loadUser = (data) => {
    this.setState({user: {

      id: data.id,
      name: data.name,
      email: data.email,
      entries: data.entries,
      joined: data.joined,
    }})
  }




  //Function to Change Routes from Signin and Home Dashboard
  onRouteChange = (route) => {

    if (route === 'signout') {

      this.setState({isSignedIn: false});

    } else if (route === 'home') {

      this.setState({isSignedIn: true});
      let words = [];
      let definitions = [];
      let defs_2 = [];
      for (let i = 0; i < 6;i++ ){
        fetch("https://wordsapiv1.p.rapidapi.com/words/?random=true&lettersMin=6&hasDetails=definitions&letterpattern=/^\S+$",
        {
          "method": "GET",
          "headers": {
            "x-rapidapi-host": "wordsapiv1.p.rapidapi.com",
            "x-rapidapi-key": "4ca040923dmshca781b0df1ed411p18c182jsn7d523d812233"
          }
        })
        .then(result => result.json())
        .then(data => {
                  this.setState({user:{words:[...this.state.user.words, data.word]}})
                  words.push(data.word);
                  definitions.push(data.results);
        })
        .catch(err => console.log(err))
        }
      console.log(definitions.length);
      if (definitions.length > 5) {
          defs_2 = [...definitions]
          for (let i = defs_2.length - 1; i > 0; i--) {
              const j = Math.floor(Math.random() * (i + 1));
              [defs_2[i], defs_2[j]] = [defs_2[j], defs_2[i]];
          }
          console.log(defs_2)
        }
      this.setState({user:{words:words,definitions:definitions,defs_shuffled:defs_2}})
  }
    this.setState({route: route});
  }

  componentWillUnmount() {
   this._isMounted = false;
 }

  render() {

    //Destructuring our states instead of using this.state
    const { isSignedIn, route} = this.state;
    const clickHandler = () => {
      console.log("Click!")
      let counter = 0;
      for (let i = 0; i<6; i++){
        let elem = document.getElementById(i);
        if (elem.children[1].children.length === 1) {
          counter++
        }
      }
      if (counter < 5){
        alert("Please drag all definitions to the right words!")
      } else {
        let counter2 = 0;
        for (let i = 0; i<6; i++){
          let elem = document.getElementById(i);
          if (parseInt(elem.children[1].children[0].id[3], 10) === i) {
            counter2++
          }
        }
        if (counter2 == 6) {
          alert("Well done!")
          if(!this._isMounted ) {this.setState({user:{words:[],definitions:[]}}) }
          let words = [];
          let definitions = [];
          for (let i = 0; i < 6;i++ ){
            fetch("https://wordsapiv1.p.rapidapi.com/words/?random=true&lettersMin=6&hasDetails=definitions&letterpattern=/^\S+$",
             {
               "method": "GET",
               "headers": {
                 "x-rapidapi-host": "wordsapiv1.p.rapidapi.com",
                 "x-rapidapi-key": "4ca040923dmshca781b0df1ed411p18c182jsn7d523d812233"
               }
             })
             .then(result => result.json())
             .then(data => {
                      if (this.state.user.words.length === 6){
                        let board = document.getElementsByClassName("board")[1]
                        for (let i = 0;i<6;i++){
                          let elem = document.getElementById(`def${i}`)
                          elem.parentElement.style.height = "50px"
                          board.appendChild(elem)
                          console.log(`elem ${i} done!`)
                        }
                        this.setState({user:{words:[data.word], definitions:[data.results]}})
                      } else {
                      this.setState({user:{words:[...this.state.user.words, data.word],
                                          definitions:[...this.state.user.definitions, data.results]}})
                      }
             })
             .catch(err => console.log(err))
             }

        } else {
          alert("Something is wrong. Try again!")
        }
      }
    }

    return (
      <div className="App">

        <Navigation isSignedIn={isSignedIn} onRouteChange={this.onRouteChange}/>


        { /* Jsx Javascript expression to know when to change the route */

         route === 'home' ? //if this is true then route to home dashboard

             <div className="flexbox">
               <Board words={this.state.user.words}/>
               <Board2 definitions={this.state.user.definitions} defs_shuffled={this.state.user.defs_shuffled}/>
               <button onClick={clickHandler}>Done!</button>
             </div>

           : (   //else if route is Signin go to Signin

             route === 'signin' ?

             <Signin loadUser={this.loadUser} onRouteChange={this.onRouteChange} />

           :

             //else return the route to Register

             <Register loadUser={this.loadUser} onRouteChange={this.onRouteChange} />
           )


        }

      </div>
    );
  }

}

export default App;
