import React from "react";
import Card from './Card';

const Board = (props) => {
  const dropOff = (e) => {
    e.preventDefault();
    const card_id = e.dataTransfer.getData('card_id');
    const card = document.getElementById(card_id);
    card.style.display = "block";
    e.target.appendChild(card);
  }

  const dragOver = (e) => {
    e.preventDefault();
  }
  return(
    <div
      id="board_1"
      className="board"
    >
      {
        props.words ? props.words.map((word,i) => {return(<Card id={i} className="card" draggable="false" text={word}/>)}) : <div>Loading...</div> 
      }
    </div>
  )
}

