import React from "react";
import DefCard from './DefCard';
import Card from './Card';

const Board2 = (props) => {
  const dropOff = (e) => {
    e.preventDefault();
    const card_id = e.dataTransfer.getData('card_id');
    const card = document.getElementById(card_id);
    card.style.display = "block";
    e.target.appendChild(card);
    e.target.style.height="auto";
  }

  const dragOver = (e) => {
    e.preventDefault();
  }
  const shuffle = (a) => {
      var j, x, i;
      for (i = a.length - 1; i > 0; i--) {
          j = Math.floor(Math.random() * (i + 1));
          x = a[i];
          a[i] = a[j];
          a[j] = x;
      }
      return a;
  }
  const shuffleCards = (obj) => {
    let a = obj.definitions.map((def,i) => {return(<DefCard id={`def${i}`} className="card" draggable="true" text={def}/>)})
    shuffle(a)
    return a;
  }
  const cards = shuffleCards(props);
  return(
    <div
      id="board_2"
      onDrop={dropOff}
      onDragOver={dragOver}
      className="board"
    >
      {
        props.definitions.length == 6 ? cards : <Card id="7" className="card" draggable="false" text="Loading..."/>
      }
    </div>
  )
}
export default Board2;
