import React from "react";
import DefCard from './DefCard';
import Card from './Card';

const Board2 = (props) => {
  console.log(props.shuffled)
  const dropOff = (e) => {
    e.preventDefault();
    const card_id = e.dataTransfer.getData('card_id');
    const card = document.getElementById(card_id);
    card.style.display = "block";
    e.target.appendChild(card);
    e.target.style.height="auto";
  }

  const dragOver = (e) => {
    e.preventDefault();
  }
  const findArrInArr = (val,arr) => {
    let hash = {};
    for(let i = 0 ; i < arr.length; i += 1) {
        hash[arr[i]] = i;
    }
    if(hash.hasOwnProperty(val)) {
     return hash[val];
    }
  }
  return(
    <div
      id="board_2"
      onDrop={dropOff}
      onDragOver={dragOver}
      className="board"
    >
      {
        props.definitions.length === 6 ? props.shuffled.map((def,i) => {return(<DefCard id={`def${findArrInArr(def,props.definitions)}`} className="card" draggable="true" text={def}/>)}) : <Card id="7" className="card" draggable="false" text="Loading..."/>
      }
    </div>
  )
}
export default Board2;
