import React from "react";
import DefCard from './DefCard';

const Board2 = (props) => {
  console.log(props)
  const dropOff = (e) => {
    e.preventDefault();
    const card_id = e.dataTransfer.getData('card_id');
    const card = document.getElementById(card_id);
    card.style.display = "block";
    e.target.appendChild(card);
    e.target.style.height="auto";
  }

  const dragOver = (e) => {
    e.preventDefault();
  }
  return(
    <div
      id="board_2"
      onDrop={dropOff}
      onDragOver={dragOver}
      className="board"
    >
      {
        props ? props.definitions.map((def,i) => {return(<DefCard id={`def${i}`} className="card" draggable="true" text={def}/>)}) : <div>Loading...</div>
      }
    </div>
  )
}
export default Board2;
