import React from "react";
import DefCard from './DefCard';
import Card from './Card';

const Board2 = (props) => {
  const {definitions} = props;
  console.log(Object.keys(definitions).length)
  const dropOff = (e) => {
    e.preventDefault();
    const card_id = e.dataTransfer.getData('card_id');
    const card = document.getElementById(card_id);
    card.style.display = "block";
    e.target.appendChild(card);
    e.target.style.height="auto";
  }
  const findArrInArr = (val,arr) => {
    let hash = {};
    for(let i = 0 ; i < arr.length; i += 1) {
        hash[arr[i]] = i;
    }
    if(hash.hasOwnProperty(val)) {
     return hash[val];
    }
  }
  let defs_shuffled = []
  for (let i of Object.keys(definitions)){
    defs_shuffled.push(definitions[i])
  }
  const dragOver = (e) => {
    e.preventDefault();
  }

  return(
    <div
      id="board_2"
      onDrop={dropOff}
      onDragOver={dragOver}
      className="board"
    >
      {
        Object.keys(definitions).length === 6 ? definitions.map((def,i) => {return(<DefCard id={`def${Object.values(definitions).indexOf(def)}`} className="card" draggable="true" text={def[1]}/>)}) : <Card id="7" className="card" draggable="false" text="Loading..."/>
      }
    </div>
  )
}
export default Board2;
